select guid, station_guid, computation_time, station_capacity, bike_sum, ev_bike_sum, company_bike_sum, over_park_sum, ev_over_park_sum, company_over_park_sum, create_time, station_status, early_peak, late_peak, order_hour_sum, hour_peak, lessee_guid, area_guid, sit_up_count from t_bike_station_hour_statistics where lessee_guid = ? and create_time >= ? and create_time < ? and (station_guid,(bike_sum+ev_bike_sum))in ( select b.station_guid, max(b.bike_sum+b.ev_bike_sum) from t_bike_station_hour_statistics b where lessee_guid = ? and create_time >= ? and create_time < ? group by b.station_guid ) group by station_guid